defs.script:
{
            "code": "flowchart TD
    A[User enters credentials] --> B{System validates format}
    B -- No --> C[Show error and return to step 1]
    C --> A
    B -- Yes --> D[Query database for user credentials]
    D -- User not found --> E[Show "Invalid credentials" error]
    D -- User found --> F{Verify password hash}
    F -- Incorrect password --> G[Increment failed attempts and check lockout]
    G -- Locked out --> H[Show lockout message]
    G -- Not locked out --> E
    F -- Correct password --> I[Generate JWT token]
    I --> J[Set session cookie and redirect to dashboard]
    J --> K[Log authentication event]",
            "theme": "default",
            "themeVariables": {
                "primaryColor": "#3B82F6",
                "background": "#ffffff"
            }
        }
